insert into example (test_id, ver_nbr, quest_nbr, exmp_nbr, exmp_txt, answer, created_date, modified_date) values ('1', '3', '7', '2', 'Use the ec2-net-utils package which will configure an additional network interface and update the routing table', 'TRUE', now(), now());
insert into example (test_id, ver_nbr, quest_nbr, exmp_nbr, exmp_txt, answer, created_date, modified_date) values ('1', '3', '7', '3', 'Use the ec2-ip-update package which can configure the network interface as well as update the secondary IP with DHCP.', 'FALSE', now(), now());
insert into example (test_id, ver_nbr, quest_nbr, exmp_nbr, exmp_txt, answer, created_date, modified_date) values ('1', '3', '7', '4', 'Use the ec2-ip-utility package which can update the routing tables as well as refresh the secondary IP using DHCP.', 'FALSE', now(), now());
